function getEnv() {
	if (workflow.scratchpad.environment == 'Development')  {
		return 'DEV';
	} else if (workflow.scratchpad.environment == 'Test') {
		return 'TEST';
	} else if (workflow.scratchpad.environment == 'Production') {
		return 'PROD';
	}
}

