function getHostNames(vmDetails) {
	var output = '';
	for (var i = 0; i < vmDetails.length; i++) {
		output += "HostName: " + vmDetails[i].hostName + "\n";
		output += "IP Address: " + vmDetails[i].privateIpAddr + "\n\n";
	}
	return output;
}

