(function () {
    workflow.scratchpad.vm_details = [];

    var childJobIdArr = workflow.scratchpad.child_job_ids.split(',');
    for (var i = 0; i < childJobIdArr.length; i++) {
        try {
            var r = new sn_ws.RESTMessageV2('x_thimi_cloud_mgmt.CloudCenter', 'GetNodeDetails');
            r.setEccParameter('skip_sensor', true);

            r.setStringParameterNoEscape('ChildjobId', childJobIdArr[i]);

            //override authentication profile
            //authentication type ='basic'/ 'oauth2'
            //r.setAuthentication(authentication type, profile name);

            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            var requestBody = r ? r.getRequestBody() : null;
            workflow.scratchpad.getHttpStatus = httpStatus;

            if (httpStatus == '200') {
                var ciObj;
                var respParsed = new global.JSON().decode(responseBody);
                var vmArr = respParsed.virtualMachines;
                for (var j = 0; j < vmArr.length; j++) {
                    ciObj = {};
                    ciObj.assignedTo = current.variables.technical_contact.toString();
                    ciObj.application = current.variables.application.toString();
                    ciObj.cloud = current.variables.cloud.toString();
                    ciObj.hostName = vmArr[j].hostName;
                    ciObj.OS = current.variables.operating_system.getDisplayValue();
                    ciObj.expPol = current.variables.end_date.getDisplayValue();
                    ciObj.privateIpAddr = vmArr[j].privateIpAddr;
                    ciObj.zone = vmArr[j].zone;
					ciObj.modelId = getModelID(workflow.scratchpad.cloud);
                    if (ciObj.hostName) {
						createCI(ciObj);
						workflow.scratchpad.vm_details.unshift(ciObj);
                    }
                }

            } else {
                workflow.scratchpad.rest_error = response.getErrorMessage();
                workflow.scratchpad.rest_req_body = requestBody;
            }

        } catch (ex) {
            var message = ex.getMessage();
            workflow.scratchpad.rest_error = message;
        }
    }

    function createCI(ciObj) {
        var ci = new GlideRecord('cmdb_ci_server');
        ci.initialize();
        ci.name = ciObj.hostName;
        ci.os = ciObj.OS;
        ci.assigned_to = ciObj.assignedTo;
        ci.model_id = ciObj.modelId;
        ci.x_thimi_cloud_mgmt_cloud = ciObj.cloud;
        ci.x_thimi_cloud_mgmt_zone = ciObj.zone;
        ci.ip_address = ciObj.privateIpAddr;
        ci.insert();

        var ci_rel = new GlideRecord('cmdb_rel_ci');
        var TYPE_RUNS_ON = '60bc4e22c0a8010e01f074cbe6bd73c3';
        ci_rel.initialize();
        ci_rel.parent = ciObj.application;
        ci_rel.type = TYPE_RUNS_ON;
        ci_rel.child = ci.sys_id;
        ci_rel.insert();

        // Update RITM Work Notes with CI link
        current.work_notes = 'Configuration Item created: ' + ci.name;
    }
	
	function getModelID(cloud) {
		var gr = new GlideRecord('x_thimi_cloud_mgmt_cloud_services_configuration');
		gr.addQuery('parameter_name', 'cmdb_model');
		gr.addQuery('cloud', cloud);
		gr.query();
		if (gr.next()) {
			return gr.parameter_value.toString();
		}
	}

})();

