(function(){
	
	try {
		var method = getMethod();
		var appParamsValue2 = gs.getProperty('x_thimi_cloud_mgmt.rest.appParamsValue2');
		var r = new sn_ws.RESTMessageV2('x_thimi_cloud_mgmt.CloudCenter', method);
		r.setEccParameter('skip_sensor', true);
		
		r.setStringParameterNoEscape('accountId', workflow.scratchpad.accountId);
		r.setStringParameterNoEscape('agingPolicyId', workflow.scratchpad.agingPolicyId);
		r.setStringParameterNoEscape('appId', workflow.scratchpad.appId);
		r.setStringParameterNoEscape('appName', workflow.scratchpad.appName);
		r.setStringParameterNoEscape('appParamsName1', workflow.scratchpad.appParamsName1);
		// r.setStringParameterNoEscape('appParamsName2', workflow.scratchpad.appParamsName2);
		r.setStringParameterNoEscape('appParamsName3', workflow.scratchpad.appParamsName3);
		r.setStringParameterNoEscape('appParamsName4', workflow.scratchpad.appParamsName4);
		r.setStringParameterNoEscape('appParamsValue1', workflow.scratchpad.appParamsValue1);
		// r.setStringParameterNoEscape('appParamsValue2', appParamsValue2);
		r.setStringParameterNoEscape('appParamsValue3', workflow.scratchpad.appParamsValue3);
		r.setStringParameterNoEscape('appParamsValue4', workflow.scratchpad.appParamsValue4);
		r.setStringParameterNoEscape('appVersion', workflow.scratchpad.appVersion);
		r.setStringParameterNoEscape('cloudPropertiesName1', workflow.scratchpad.cloudPropertiesName1);
		r.setStringParameterNoEscape('cloudPropertiesName2', workflow.scratchpad.cloudPropertiesName2);
		r.setStringParameterNoEscape('cloudPropertiesName3', workflow.scratchpad.cloudPropertiesName3);
		r.setStringParameterNoEscape('cloudPropertiesName4', workflow.scratchpad.cloudPropertiesName4);
		r.setStringParameterNoEscape('cloudPropertiesName5', workflow.scratchpad.cloudPropertiesName5);
		r.setStringParameterNoEscape('cloudPropertiesName6', workflow.scratchpad.cloudPropertiesName6);
		r.setStringParameterNoEscape('cloudPropertiesName7', workflow.scratchpad.cloudPropertiesName7);
		r.setStringParameterNoEscape('cloudPropertiesName8', workflow.scratchpad.cloudPropertiesName8);
		r.setStringParameterNoEscape('cloudPropertiesValue1', workflow.scratchpad.cloudPropertiesValue1);
		r.setStringParameterNoEscape('cloudPropertiesValue2', workflow.scratchpad.cloudPropertiesValue2);
		r.setStringParameterNoEscape('cloudPropertiesValue3', workflow.scratchpad.cloudPropertiesValue3);
		r.setStringParameterNoEscape('cloudPropertiesValue4', workflow.scratchpad.cloudPropertiesValue4);
		r.setStringParameterNoEscape('cloudPropertiesValue5', workflow.scratchpad.cloudPropertiesValue5);
		r.setStringParameterNoEscape('cloudPropertiesValue6', workflow.scratchpad.cloudPropertiesValue6);
		r.setStringParameterNoEscape('cloudPropertiesValue7', workflow.scratchpad.cloudPropertiesValue7);
		r.setStringParameterNoEscape('cloudPropertiesValue8', workflow.scratchpad.cloudPropertiesValue8);
		r.setStringParameterNoEscape('cloudRegionId', workflow.scratchpad.cloudRegionId);
		r.setStringParameterNoEscape('environmentId', workflow.scratchpad.environmentId);
		r.setStringParameterNoEscape('instance', workflow.scratchpad.instance);
		r.setStringParameterNoEscape('networkName1', workflow.scratchpad.networkName1);
		r.setStringParameterNoEscape('networkName2', workflow.scratchpad.networkName2);
		r.setStringParameterNoEscape('networkName3', workflow.scratchpad.networkName3);
		r.setStringParameterNoEscape('networkValue1', workflow.scratchpad.networkValue1);
		r.setStringParameterNoEscape('networkValue2', workflow.scratchpad.networkValue2);
		r.setStringParameterNoEscape('networkValue3', workflow.scratchpad.networkValue3);
		r.setStringParameterNoEscape('numNodesToLaunch', workflow.scratchpad.numNodesToLaunch);
		r.setStringParameterNoEscape('tierId1', workflow.scratchpad.tierId1);
		if (workflow.scratchpad.load_balancing) {
			r.setStringParameterNoEscape('tierId2', workflow.scratchpad.tierId2);
			r.setStringParameterNoEscape('lbInstance', workflow.scratchpad.lbInstance);
		} else if (workflow.scratchpad.pan_policy) {
			r.setStringParameterNoEscape('tierId2', workflow.scratchpad.tierId2);
			r.setStringParameterNoEscape('tierId3', workflow.scratchpad.tierId3);
		}
		
		//override authentication profile
		//authentication type ='basic'/ 'oauth2'
		//r.setAuthentication(authentication type, profile name);
		
		var response = r.execute();
		var responseBody = response.getBody();
		var httpStatus = response.getStatusCode();
		var requestBody = r ? r.getRequestBody() : null;
		workflow.scratchpad.postHttpStatus = httpStatus;
		
		if (requestBody) {
			// Obfuscate Password in appParamsValue2
			requestBody = requestBody.replace(appParamsValue2, '************');
		}
		
		if (httpStatus == '201') {
			var respParsed = new global.JSON().decode(responseBody);
			if (respParsed) {
				workflow.scratchpad.job_id = respParsed.id;

				var childJobsObj = respParsed.childJobs;
				workflow.scratchpad.child_job_ids = [];
				for (var i = 0; i < childJobsObj.length; i++) {
					workflow.scratchpad.child_job_ids.push(childJobsObj[i].id);
				}
				workflow.scratchpad.child_job_ids = workflow.scratchpad.child_job_ids.join();

				createJSLog(workflow.scratchpad.job_id, workflow.scratchpad.child_job_ids, current.sys_id, requestBody, responseBody);
			} else {
				workflow.scratchpad.response_missing = true;
			}
		} else {
			workflow.scratchpad.rest_error = response.getErrorMessage();
			workflow.scratchpad.rest_req_body = requestBody;
		}
		
	}
	catch(ex) {
		var message = ex.getMessage();
		workflow.scratchpad.rest_error = message;
	}
	
	function createJSLog(jobId, childJobIds, ritmId, requestBody, responseBody) {
		var js = new GlideRecord('x_thimi_cloud_mgmt_cloud_services_job_status');
		js.initialize();
		js.job_id = jobId;
		js.child_job_ids = childJobIds;
		js.requested_item = ritmId;
		js.submitjob_request = requestBody;
		js.submitjob_response = responseBody;
		js.status = 'requested';
		js.insert();
	}
	
	function getMethod() {
		if (workflow.scratchpad.load_balancing) {
			return 'SubmitJobLB';
		} else if (workflow.scratchpad.pan_policy) {
			return 'SubmitJobFW';
		} else {
			return 'SubmitJob';
		}
	}
	
})();

