    function getProperty(propName, checkCloud, checkEnvironment, dependentVar, checkExternal) {
		
        var prop = new GlideRecord('x_thimi_cloud_mgmt_cloud_services_configuration');
        prop.addQuery('parameter_name', propName);
        if (checkCloud) {
            prop.addQuery('cloud', workflow.scratchpad.cloud);
        }
        if (checkEnvironment) {
			var env;
			// Only 'environmentId' differentiates between Sub-Prod environments
			if (propName == 'environmentId') {
				env = workflow.scratchpad.environment;
			} else {
				env = workflow.scratchpad.environment == 'Production' ? 'Production' : 'Development';
			}
            prop.addQuery('environment', env);
        }
        if (dependentVar) {
			var dependentVarVal;
			if (dependentVar == 'server_configuration') {
				dependentVarVal = (current.variables[dependentVar]).toString().replace(/_.*/, ''); // Remove _Dev and _Prod designations
			} else {
				dependentVarVal = current.variables[dependentVar];
			}			
            prop.addQuery('dependent_variable.name', dependentVar);
            prop.addQuery('dependent_value', dependentVarVal);
        }
        if (checkExternal) {
            prop.addQuery('externally_facing', workflow.scratchpad.externally_facing);
        }
        prop.query();
        if (prop.next()) {
            return prop.parameter_value;
        }
		return '';
		
    }
	
	function getVMwareProperty(propName, environmentId) {
		
	    if (workflow.scratchpad.cloud == 'On-Premise') {
			var isDMZ = workflow.scratchpad.externally_facing;
			
	        var prop = new GlideRecord('x_thimi_cloud_mgmt_cloud_services_configuration');
	        prop.addQuery('parameter_name', 'environmentId');
	        prop.addQuery('parameter_value', environmentId);
	        prop.query();
	        if (prop.next()) {
				if (propName == 'cloudPropertiesValue4' && isDMZ) {
					return prop.datastore_cluster_dmz;
				} else if (propName == 'cloudPropertiesValue4' && !isDMZ) {
					return prop.datastore_cluster;
				} else if (propName == 'cloudPropertiesValue6' && isDMZ) {
	                return prop.cluster_dmz;
				} else if (propName == 'cloudPropertiesValue6' && !isDMZ) {
					return prop.cluster;
	            } else if (propName == 'networkValue1' && isDMZ) {
	                return prop.network_public;
	            } else if (propName == 'networkValue1' && !isDMZ) {
	                return prop.network_private;
	            }
	        }
	    }
	    return '';
		
	}
	
	function getNetworkProperty(propName, checkCloud, checkEnvironment, dependentVar, checkExternal, environmentId) {
		
		if (workflow.scratchpad.cloud == 'On-Premise') {
				return getVMwareProperty(propName, environmentId);
		} else {
				return getProperty(propName, checkCloud, checkEnvironment, dependentVar, checkExternal);
		}
		
	}

