	function getAppName(numberStr) {
		var appName = '', 
			instance = gs.getProperty('instance_name');
		
		if (instance == 'irvinedev') {
			appName += 'D-';
		} else if (instance == 'irvinetest') {
			appName += 'T-';			
		}
		appName += numberStr;
		
		return appName;
	}

