function ifScript() {
	var isVMware = workflow.scratchpad.cloud == 'On-Premise';
	var isDMZ = workflow.scratchpad.externally_facing;
	
	if (isVMware && isDMZ) {
		return 'yes';
	}
	return 'no';
}

